script:
$(document).ready(function(){
    $("ul#top_menu li a").click(function(){
        if ($("ul#top_menu li a span").text()=='SHOP'){
            console.log("On Shop Pg");
        } else if ($("ul#top_menu li a span").text()=='SALE'){
            console.log("On Sale Pg");
        }
    });
});

$(document).ready(function(){
    if(window.location.pathname == '/shop'){
        console.log("Shop Pg");
    } else if (window.location.pathname == '/sale'){
        console.log("Sale Pg");
    }
});
